from django.http import HttpResponse
from django.views import View
import base64


class PlaceholderImageView(View):
    img = base64.b64decode("")
    img_file_name = "placeholder.png"

    def get(self, request, **kwargs):
        resp = HttpResponse(self.img, content_type="application/png")
        resp["Content-Disposition"] = f"attachment; filename={self.img_file_name}"
        return resp
